import React, { useState, useEffect, useCallback, memo } from 'react';

interface ChessPiece {
  type: 'king' | 'queen' | 'rook' | 'bishop' | 'knight' | 'pawn';
  color: 'white' | 'black';
}

interface ChessSquare {
  piece?: ChessPiece;
}

const initialBoard: ChessSquare[][] = Array.from({ length: 8 }, () =>
  Array.from({ length: 8 }, (_, colIndex, rowIndex) => {
    const row = rowIndex + 1;
    let piece: ChessPiece | undefined;

    if (row === 1 || row === 8) {
      const color = row === 1 ? 'white' : 'black';
      piece = colIndex === 0 || colIndex === 7 ? { type: 'rook', color } : undefined;
      piece = colIndex === 1 || colIndex === 6 ? { type: 'knight', color } : piece;
      piece = colIndex === 2 || colIndex === 5 ? { type: 'bishop', color } : piece;
      piece = colIndex === 3 ? { type: 'queen', color } : piece;
      piece = colIndex === 4 ? { type: 'king', color } : piece;
    } else if (row === 2 || row === 7) {
      piece = { type: 'pawn', color: row === 2 ? 'white' : 'black' };
    }

    return { piece };
  })
);

const ChessBoard: React.FC = () => {
  const [board, setBoard] = useState<ChessSquare[][]>(initialBoard);
  const [selectedPiece, setSelectedPiece] = useState<{ row: number; col: number } | null>(null);

  const handleSquareClick = useCallback(
    (row: number, col: number) => {
      if (selectedPiece) {
        // Move piece logic here
        const newBoard = JSON.parse(JSON.stringify(board));
        newBoard[row][col].piece = newBoard[selectedPiece.row][selectedPiece.col].piece;
        newBoard[selectedPiece.row][selectedPiece.col].piece = undefined;
        setBoard(newBoard);
        setSelectedPiece(null);
      } else if (board[row][col].piece) {
        setSelectedPiece({ row, col });
      }
    },
    [board, selectedPiece]
  );

  const renderPiece = (piece: ChessPiece) => {
    const pieceMap: { [key in ChessPiece['type']]: string } = {
      king: '♚♔',
      queen: '♛♕',
      rook: '♜♖',
      bishop: '♝♗',
      knight: '♞♘',
      pawn: '♟♙',
    };

    const colorMap: { [key in ChessPiece['color']]: string } = {
      white: 'white',
      black: 'black',
    };

    return <div className={`text-4xl ${colorMap[piece.color]}`}>{pieceMap[piece.type]}</div>;
  };

  return (
    <div className="grid grid-cols-8 gap-1">
      {board.map((row, rowIndex) =>
        row.map((square, colIndex) => (
          <div
            key={`${rowIndex}-${colIndex}`}
            className={`flex items-center justify-center w-12 h-12 ${
              (rowIndex + colIndex) % 2 === 0 ? 'bg-gray-200' : 'bg-gray-700'
            } cursor-pointer hover:bg-gray-300 active:bg-gray-400`}
            onClick={() => handleSquareClick(rowIndex, colIndex)}
          >
            {square.piece && renderPiece(square.piece)}
          </div>
        ))
      )}
    </div>
  );
};

const App: React.FC = () => {
  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 text-white">
      <h1 className="text-4xl font-bold mb-4">Simple Chess Game</h1>
      <ChessBoard />
    </div>
  );
};

export default App;
