import React, { useState } from 'react';

interface CalculatorButtonProps {
  value: string;
  onClick: (value: string) => void;
  className?: string;
}

const CalculatorButton: React.FC<CalculatorButtonProps> = ({ value, onClick, className }) => (
  <button
    className={`calculator-button ${className}`}
    onClick={() => onClick(value)}
    aria-label={`Button ${value}`}
  >
    {value}
  </button>
);

const App: React.FC = () => {
  const [input, setInput] = useState<string>('');
  const [result, setResult] = useState<string>('');

  const handleButtonClick = (value: string) => {
    if (value === 'C') {
      setInput('');
      setResult('');
    } else if (value === '=') {
      try {
        setResult(eval(input).toString()); // For simplicity, using eval. In production, use a safer alternative.
      } catch (error) {
        setResult('Error');
      }
    } else {
      setInput(input + value);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-900 text-white">
      <div className="calculator bg-gray-800 p-6 rounded-lg shadow-lg w-full max-w-sm">
        <div className="calculator-display bg-gray-700 p-4 rounded-lg mb-4 text-right overflow-hidden">
          <div className="text-2xl">{input || '0'}</div>
          <div className="text-lg opacity-75">{result}</div>
        </div>
        <div className="calculator-buttons grid grid-cols-4 gap-2">
          {['7', '8', '9', '/', '4', '5', '6', '*', '1', '2', '3', '-', '0', '.', '=', '+', 'C'].map(
            (value) => (
              <CalculatorButton
                key={value}
                value={value}
                onClick={handleButtonClick}
                className={`${
                  ['=', '+', '-', '*', '/'].includes(value) ? 'bg-blue-500' : 'bg-gray-600'
                } hover:bg-opacity-80 transition duration-300 ease-in-out`}
              />
            ),
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
```

