        </button>
        <button
          onClick={() => handleSexChange('female')}
          className={`p-3 px-6 rounded-full ${sex === 'female' ? 'bg-pink-600 text-white' : 'bg-gray-200 text-gray-700'}`}
        >
          Female
        </button>
      </div>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
        {filteredUsers.map(user => (
          <div key={user.id} className="bg-white rounded-lg shadow-lg p-6 hover:shadow-2xl transition-shadow duration-300">
            <img src={user.image} alt={user.name} className="w-full h-48 object-cover rounded-t-lg" />
            <div className="mt-4">
