const initialBoard = Array.from({ length: 8 }, (_, row) =>
  Array.from({ length: 8 }, (_, col) => {
    if (row === 1) return 'bp'; // Black pawns
    if (row === 6) return 'wp'; // White pawns
    if (row === 0 || row === 7) {
      const pieces = ['r', 'n', 'b', 'q', 'k', 'b', 'n', 'r'];
      return row === 0 ? pieces.map(p => 'b' + p) : pieces.map(p => 'w' + p);
    }
    return null;
  })
);

