const ChessBoard: React.FC = () => {
  const [board, setBoard] = useState(initialBoard);
  const [selectedPiece, setSelectedPiece] = useState<{ row: number; col: number } | null>(null);
  const [turn, setTurn] = useState<'white' | 'black'>('white');

  const handlePieceClick = useCallback(
    (row: number, col: number) => {
      const piece = board[row][col];
      if (piece?.startsWith(turn)) {
        setSelectedPiece({ row, col });
      } else if (selectedPiece && isValidMove(selectedPiece.row, selectedPiece.col, row, col)) {
        movePiece(selectedPiece.row, selectedPiece.col, row, col);
        setSelectedPiece(null);
        setTurn((prevTurn) => (prevTurn === 'white' ? 'black' : 'white'));
      }
    },
    [board, selectedPiece, turn]
  );

  const isValidMove = useCallback(
    (startRow: number, startCol: number, endRow: number, endCol: number) => {
      const piece = board[startRow][startCol];
      if (!piece) return false;

      const targetPiece = board[endRow][endCol];
      if (targetPiece && targetPiece.startsWith(piece[0])) return false;

      const isWhite = piece.startsWith('w');
      const isBlack = piece.startsWith('b');

      switch (piece[1]) {
        case 'p': // Pawn
          const direction = isWhite ? -1 : 1;
          if (
            endCol === startCol &&
            (endRow === startRow + direction || (startRow === (isWhite ? 6 : 1) && endRow === startRow + 2 * direction)) &&
            !board[endRow][endCol]
          ) {
            return true;
          }
          if (
            Math.abs(endCol - startCol) === 1 &&
            endRow === startRow + direction &&
            board[endRow][endCol]?.startsWith(isBlack ? 'w' : 'b')
          ) {
            return true;
          }
          break;
        case 'r': // Rook
          if (startRow === endRow || startCol === endCol) {
            const stepRow = Math.sign(endRow - startRow);
            const stepCol = Math.sign(endCol - startCol);
            for (let i = 1; i < Math.max(Math.abs(endRow - startRow), Math.abs(endCol - startCol)); i++) {
              if (board[startRow + stepRow * i][startCol + stepCol * i]) {
                return false;
              }
            }
            return true;
          }
          break;
        case 'n': // Knight
          if (
            (Math.abs(endRow - startRow) === 2 && Math.abs(endCol - startCol) === 1) ||
            (Math.abs(endRow - startRow) === 1 && Math.abs(endCol - startCol) === 2)
          ) {
            return true;
          }
          break;
        case 'b': // Bishop
          if (Math.abs(endRow - startRow) === Math.abs(endCol - startCol)) {
            const stepRow = Math.sign(endRow - startRow);
            const stepCol = Math.sign(endCol - startCol);
            for (let i = 1; i < Math.abs(endRow - startRow); i++) {
              if (board[startRow + stepRow * i][startCol + stepCol * i]) {
                return false;
              }
            }
            return true;
          }
          break;
        case 'q': // Queen
          if (
            startRow === endRow ||
            startCol === endCol ||
            Math.abs(endRow - startRow) === Math.abs(endCol - startCol)
          ) {
            const stepRow = Math.sign(endRow - startRow);
            const stepCol = Math.sign(endCol - startCol);
            for (let i = 1; i < Math.max(Math.abs(endRow - startRow), Math.abs(endCol - startCol)); i++) {
              if (board[startRow + stepRow * i][startCol + stepCol * i]) {
                return false;
              }
            }
            return true;
          }
          break;
        case 'k': // King
          if (
            Math.abs(endRow - startRow) <= 1 &&
            Math.abs(endCol - startCol) <= 1
          ) {
            return true;
          }
          break;
      }
      return false;
    },
    [board]
  );

  const movePiece = useCallback(
    (startRow: number, startCol: number, endRow: number, endCol: number) => {
      const newBoard = board.map((row) => [...row]);
      newBoard[endRow][endCol] = newBoard[startRow][startCol];
      newBoard[startRow][startCol] = null;
      setBoard(newBoard);
    },
    [board]
  );

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900">
      <h1 className="text-4xl font-bold text-white mb-8">Chess Game</h1>
      <div className="grid grid-cols-8 gap-1">
        {board.map((row, rowIndex) =>
          row.map((piece, colIndex) => (
            <div
              key={`${rowIndex}-${colIndex}`}
              className={`w-12 h-12 ${
                (rowIndex + colIndex) % 2 === 0 ? 'bg-gray-800' : 'bg-gray-600'
              } flex items-center justify-center`}
              onClick={() => handlePieceClick(rowIndex, colIndex)}
            >
              <Piece piece={piece} onClick={() => handlePieceClick(rowIndex, colIndex)} />
            </div>
          ))
        )}
      </div>
    </div>
  );
};

const App: React.FC = () => {
  return (
    <ChessBoard />
  );
};

export default App;
```

