import pytest
from solution import has_close_elements

def test_no_close_elements():
    assert has_close_elements([1.0, 2.0, 3.0], 0.5) == False

def test_has_close_elements():
    assert has_close_elements([1.0, 2.8, 3.0, 4.0], 0.3) == True

def test_empty_list():
    assert has_close_elements([], 0.5) == False

def test_single_element():
    assert has_close_elements([1.0], 0.5) == False

def test_identical_elements():
    assert has_close_elements([1.0, 1.0], 0.1) == True
