from fastapi import FastAPI, HTTPException
from pydantic import BaseModel
from typing import List

app = FastAPI()

class Sequence(BaseModel):
    sequence: List[int]


@app.post("/calculate/sum")
async def calculate_sum(sequence_data: Sequence):
    try:
        sequence = sequence_data.sequence
        if not all(isinstance(x, int) for x in sequence):
            raise HTTPException(status_code=400, detail="Invalid input. The 'sequence' must be a list of integers.")
        sum_of_sequence = sum(sequence)
        return {"sum": sum_of_sequence}
    except Exception as e:
        raise HTTPException(status_code=500, detail=str(e))

