import React, { useState } from 'react';

function SumCalculator() {
  const [sequence, setSequence] = useState('');
  const [sum, setSum] = useState(null);
  const [error, setError] = useState(null);

  const calculateSum = async () => {
    try {
      const sequenceArray = sequence.split(',').map(Number);
      if (sequenceArray.some(isNaN)) {
        setError('Invalid input: Please enter a comma-separated list of integers.');
        setSum(null);
        return;
      }

      const response = await fetch('/calculate/sum', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ sequence: sequenceArray }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setError(errorData.detail || 'An error occurred.');
        setSum(null);
        return;
      }

      const data = await response.json();
      setSum(data.sum);
      setError(null);
    } catch (err) {
      setError('Failed to connect to the server.');
      setSum(null);
    }
  };

  return (
    <div>
      <label htmlFor="sequence">Enter a comma-separated sequence of integers:</label>
      <input
        type="text"
        id="sequence"
        value={sequence}
        onChange={(e) => setSequence(e.target.value)}
      />
      <button onClick={calculateSum}>Calculate Sum</button>

      {error && <p style={{ color: 'red' }}>Error: {error}</p>}
      {sum !== null && <p>Sum: {sum}</p>}
    </div>
  );
}

export default SumCalculator;

