import React from 'react';
import { render, screen, fireEvent, waitFor } from '@testing-library/react';
import SumCalculator from './SumCalculator';

// Mock the fetch function
global.fetch = jest.fn();

beforeEach(() => {
  fetch.mockClear();
});

test('renders the SumCalculator component', () => {
  render(<SumCalculator />);
  expect(screen.getByLabelText(/Enter a comma-separated sequence of integers:/i)).toBeInTheDocument();
  expect(screen.getByText(/Calculate Sum/i)).toBeInTheDocument();
});

test('calculates and displays the sum correctly', async () => {
  render(<SumCalculator />);
  const inputElement = screen.getByLabelText(/Enter a comma-separated sequence of integers:/i);
  const calculateButton = screen.getByText(/Calculate Sum/i);

  // Mock the successful fetch response
  fetch.mockResolvedValueOnce({
    ok: true,
    json: async () => ({ sum: 15 }),
  });

  fireEvent.change(inputElement, { target: { value: '1,2,3,4,5' } });
  fireEvent.click(calculateButton);

  await waitFor(() => {
    expect(screen.getByText(/Sum: 15/i)).toBeInTheDocument();
  });
});

test('displays an error message for invalid input', async () => {
  render(<SumCalculator />);
  const inputElement = screen.getByLabelText(/Enter a comma-separated sequence of integers:/i);
  const calculateButton = screen.getByText(/Calculate Sum/i);

  fireEvent.change(inputElement, { target: { value: '1,a,3,4,5' } });
  fireEvent.click(calculateButton);

  await waitFor(() => {
    expect(screen.getByText(/Invalid input:/i)).toBeInTheDocument();
  });
});

test('displays an error message when the API call fails', async () => {
  render(<SumCalculator />);
  const inputElement = screen.getByLabelText(/Enter a comma-separated sequence of integers:/i);
  const calculateButton = screen.getByText(/Calculate Sum/i);

  // Mock the failed fetch response
  fetch.mockResolvedValueOnce({
    ok: false,
    json: async () => ({ detail: 'API Error' }),
  });

  fireEvent.change(inputElement, { target: { value: '1,2,3,4,5' } });
  fireEvent.click(calculateButton);

  await waitFor(() => {
    expect(screen.getByText(/Error: API Error/i)).toBeInTheDocument();
  });
});

