    const numbersArray = numbers.split(/[,\s]+/).map(Number).filter(Boolean);
    onCalculate(numbersArray);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Enter a sequence of integers (comma or space separated):
        <input
          type="text"
          value={numbers}
          onChange={(e) => setNumbers(e.target.value)}
        />
      </label>
      <button type="submit">Calculate</button>
    </form>
  );
}

export default InputForm;

