import React from 'react';
import { render, screen, fireEvent, waitFor } from '@testing-library/react';
import App from './App';
import fetchMock from 'jest-fetch-mock';

fetchMock.enableMocks();

describe('App Component', () => {
  beforeEach(() => {
    fetch.resetMocks();
  });

  it('renders the input form and calculate button', () => {
    render(<App />);
    expect(screen.getByLabelText(/Enter a sequence of integers/i)).toBeInTheDocument();
    expect(screen.getByText(/Calculate/i)).toBeInTheDocument();
  });

  it('calculates and displays the sum correctly', async () => {
    fetch.mockResponseOnce(JSON.stringify({ sum: 15 }));
    render(<App />);
    const inputElement = screen.getByLabelText(/Enter a sequence of integers/i);
    const calculateButton = screen.getByText(/Calculate/i);

    fireEvent.change(inputElement, { target: { value: '1,2,3,4,5' } });
    fireEvent.click(calculateButton);

    await waitFor(() => {
      expect(screen.getByText(/Sum: 15/i)).toBeInTheDocument();
    });
  });

  it('displays an error message when the API returns an error', async () => {
    fetch.mockResponseOnce(JSON.stringify({ detail: 'Invalid input' }), { status: 400 });
    render(<App />);
    const inputElement = screen.getByLabelText(/Enter a sequence of integers/i);
    const calculateButton = screen.getByText(/Calculate/i);

    fireEvent.change(inputElement, { target: { value: 'a,b,c' } });
    fireEvent.click(calculateButton);

    // Mocking window.alert is generally bad practice, but for this example it's the easiest way to test the alert.
    const alertMock = jest.spyOn(window, 'alert').mockImplementation();

    await waitFor(() => {
      expect(alertMock).toHaveBeenCalledWith('Invalid input');
    });

    alertMock.mockRestore(); // Restore the original alert function
  });
});

