from fastapi import APIRouter, HTTPException
from typing import List
from app.schemas import sum_schema
from app.services import calculation_service

router = APIRouter()

@router.post("/api/v1/calculate-sum", response_model=sum_schema.SumResponse, tags=["Calculations"])
async def calculate_sum(sequence: List[int]):
    try:
        sum_result = calculation_service.sum_sequence(sequence)
        return sum_schema.SumResponse(sum=sum_result)
    except Exception as e:
        raise HTTPException(status_code=500, detail=str(e))

