import React from 'react';
import { render, screen, fireEvent, waitFor } from '@testing-library/react';
import SumCalculatorForm from './SumCalculatorForm';
import apiService from '../services/apiService';

// Mock the apiService
jest.mock('../services/apiService');

describe('SumCalculatorForm', () => {
  it('renders the form with input and button', () => {
    const setSum = jest.fn();
    render(<SumCalculatorForm setSum={setSum} />);

    const inputElement = screen.getByLabelText(/Enter numbers/i);
    const buttonElement = screen.getByRole('button', { name: /Calculate Sum/i });

    expect(inputElement).toBeInTheDocument();
    expect(buttonElement).toBeInTheDocument();
  });

  it('calls the apiService with the correct numbers and updates the sum on successful submission', async () => {
    const setSum = jest.fn();
    apiService.calculateSum.mockResolvedValue({ sum: 15 }); // Mock successful API response
    render(<SumCalculatorForm setSum={setSum} />);

    const inputElement = screen.getByLabelText(/Enter numbers/i);
    const buttonElement = screen.getByRole('button', { name: /Calculate Sum/i });

    fireEvent.change(inputElement, { target: { value: '1,2,3,4,5' } });
    fireEvent.click(buttonElement);

    await waitFor(() => {
      expect(apiService.calculateSum).toHaveBeenCalledWith([1, 2, 3, 4, 5]);
      expect(setSum).toHaveBeenCalledWith(15);
    });
  });

  it('displays an alert on API error', async () => {
    const setSum = jest.fn();
    apiService.calculateSum.mockRejectedValue(new Error('API error')); // Mock API error
    render(<SumCalculatorForm setSum={setSum} />);

    const inputElement = screen.getByLabelText(/Enter numbers/i);
    const buttonElement = screen.getByRole('button', { name: /Calculate Sum/i });

    fireEvent.change(inputElement, { target: { value: '1,2,a,4,5' } });
    fireEvent.click(buttonElement);

    // Mock the window.alert function
    const alertMock = jest.spyOn(window, 'alert').mockImplementation(() => {});
    await waitFor(() => {
        expect(alertMock).toHaveBeenCalledWith('Error calculating sum. Please check the console.');
    });
    alertMock.mockRestore();
  });
});

