    const mockResponse = { sum: 15 };
    fetch.mockResponseOnce(JSON.stringify(mockResponse));

    const numbers = [1, 2, 3, 4, 5];
    const result = await apiService.calculateSum(numbers);

    expect(fetch).toHaveBeenCalledWith('/api/v1/calculate-sum', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(numbers),
    });

    expect(result).toEqual(mockResponse);
  });

  it('throws an error on failed request', async () => {
    fetch.mockReject(new Error('Failed to fetch'));

    const numbers = [1, 2, 3, 4, 5];

    await expect(apiService.calculateSum(numbers)).rejects.toThrow('Failed to fetch');
  });
});

